mapper.select:
select

from member_benefit_record
where member_id = #{memberId,jdbcType=BIGINT}
and benefit_type = #{benefitType,jdbcType=TINYINT}
order by issue_time desc

select

from member_benefit_record
where member_id = #{memberId,jdbcType=BIGINT}
and status = #{status,jdbcType=TINYINT}
order by issue_time desc

mapper.update:
update member_benefit_record
member_id = #{memberId,jdbcType=BIGINT}, benefit_type = #{benefitType,jdbcType=TINYINT}, benefit_value = #{benefitValue,jdbcType=VARCHAR}, status = #{status,jdbcType=TINYINT}, expire_time = #{expireTime,jdbcType=TIMESTAMP}, issue_time = #{issueTime,jdbcType=TIMESTAMP}, use_time = #{useTime,jdbcType=TIMESTAMP}, reason = #{reason,jdbcType=VARCHAR}, update_time = CURRENT_TIMESTAMP,
where id = #{id,jdbcType=BIGINT}
