mapper.update:
update points_account
member_id = #{memberId,jdbcType=BIGINT}, total_points = #{totalPoints,jdbcType=INTEGER}, available_points = #{availablePoints,jdbcType=INTEGER}, frozen_points = #{frozenPoints,jdbcType=INTEGER}, expired_points = #{expiredPoints,jdbcType=INTEGER}, update_time = CURRENT_TIMESTAMP,
where id = #{id,jdbcType=BIGINT}

update points_account
set total_points = total_points - #{points,jdbcType=INTEGER},
    available_points = available_points - #{points,jdbcType=INTEGER},
    update_time = CURRENT_TIMESTAMP
where member_id = #{memberId,jdbcType=BIGINT}
and available_points >= #{points,jdbcType=INTEGER}

update points_account
set available_points = available_points - #{points,jdbcType=INTEGER},
    frozen_points = frozen_points + #{points,jdbcType=INTEGER},
    update_time = CURRENT_TIMESTAMP
where member_id = #{memberId,jdbcType=BIGINT}
and available_points >= #{points,jdbcType=INTEGER}

update points_account
set available_points = available_points + #{points,jdbcType=INTEGER},
    frozen_points = frozen_points - #{points,jdbcType=INTEGER},
    update_time = CURRENT_TIMESTAMP
where member_id = #{memberId,jdbcType=BIGINT}
and frozen_points >= #{points,jdbcType=INTEGER}
